    return fromUInt(toUInt(a) >> (toUInt(b) & 0x1f));
}

//%
TNumber asrs(TNumber a, TNumber b) {
    return fromInt(toInt(a) >> (toInt(b) & 0x1f));
}

//%
TNumber eors(TNumber a, TNumber b){BITOP(^)}

//%
