    WCompas()
        : i2c(getI2C(LOOKUP_PIN(LSM6DSL_SDA), LOOKUP_PIN(LSM6DSL_SCL))),
          coordinateSpace(codal::CoordinateSystem::SIMPLE_CARTESIAN),
          magnetometer(new codal::LIS3MDL_Magnetometer(i2c, 0x3C, coordinateSpace)) {
