//% y.min=0 y.max=1
//% parts=lcd_i2c trackArgs=0
void ShowStringAtPos(String s, uint8_t x, uint8_t y) {
    if (getWLCDI2C()->lcd == nullptr)
        return;

    getWLCDI2C()->lcd->setCursor(x, y);
    ShowString(s);
}
